uniswap_obj = Uniswap(address=MASTER_ADDRESS, private_key=MASTER_PRIVATE_KEY, version=3, provider=RPC_URL)


def add_liquidity():
    usdc_balance = usdc.functions.balanceOf(MASTER_ADDRESS).call()
    usdt_balance = usdt.functions.balanceOf(MASTER_ADDRESS).call()
    
    # The data returned by function 'balanceOf' has small difference with real number because of rounding
    # So I use '< 100' and '- 100' instead of strict equal
    if usdc_balance < 100 and usdt_balance < 100:
        print("No USDC or USDT!")
        return
    else:
        print("Providing Liquidity")
        if usdc_balance >= 10 * 10 ** 6:
            uniswap_obj.mint_liquidity(contract, 10 * 10 ** 6, 0, 10, 100)
        elif usdc_balance > 100:
            uniswap_obj.mint_liquidity(contract, (usdc_balance-100), 0, 10, 100)

        if usdt_balance >= 10 * 10 ** 6:
            uniswap_obj.mint_liquidity(contract, 0, 10 * 10 ** 6, -100, -10)
        elif usdt_balance > 100:
            uniswap_obj.mint_liquidity(contract, 0, (usdt_balance-100), -100, -10)


def handle_event(event):
    event_json = json.loads(web3.toJSON(event))
    print("**************************************")
    print(datetime.datetime.now())
    # # print(event_json)
    print("USDC: " + str(event_json["args"]["amount0"]))
    print("USDT: " + str(event_json["args"]["amount1"]))
    print("**************************************")
    print()
    add_liquidity()

async def log_loop(event_filter, poll_interval):
    while True:
        for Swap in event_filter.get_new_entries():
            handle_event(Swap)
        await asyncio.sleep(poll_interval)


def main():
    event_filter = contract.events.Swap.createFilter(fromBlock='latest')
    #block_filter = web3.eth.filter('latest')
    # tx_filter = web3.eth.filter('pending')
    loop = asyncio.get_event_loop()
    try:
        loop.run_until_complete(
            asyncio.gather(
                log_loop(event_filter, 2)))
                # log_loop(block_filter, 2),
                # log_loop(tx_filter, 2)))
    finally:
        # close loop to free up system resources
        loop.close()


